import React, { useState } from "react";
import FormElement from "../FormElement";
import { useEffect } from "react";

export const ContactForm = ({
  name,
  setName,
  phone,
  setPhone,
  email,
  setEmail,
  handleSubmit
}) => {
  const [nameChange, setNameChange] = useState("")
  const [phoneChange, setPhoneChange] = useState("")
  const [emailChange, setEmailChange] = useState("")

  const handleSubmitForm = (e) => {
    e.preventDefault();
    setName(nameChange);
    setEmail(emailChange);
    setPhone(phoneChange);
    
    setNameChange("")
    setEmailChange("")
    setPhoneChange("")
  }

  useEffect(()=>{
    handleSubmit(name,email,phone)
  },[nameChange,emailChange,phoneChange])

  return (
    <form onSubmit={handleSubmitForm}>

      <FormElement
        type="text"
        value={nameChange}
        label="Name:"
        id="name"
        handleChange={e => setNameChange(e.target.value)} />


      <FormElement
        type="phone"
        value={phoneChange}
        label="Phone:"
        id="phone"
        handleChange={e => setPhoneChange(e.target.value)}
      />

      <FormElement
        type="email"
        value={emailChange}
        label="Email:"
        id="email"
        handleChange={e => setEmailChange(e.target.value)}
      />

      <FormElement
        type="submit"
      />

    </form>
  );
};
